import styled from 'styled-components';

export const Button = styled.button`
    //normal styling
    background: ${({primary}) => (primary ? '#000d1a' : '#cd853f')};
    white-space: nowrap;
    outline: none;
    border: none;
    min-width: 100px;
    max-width: 200px;
    cursor: pointer;
    transition: 0.3s;
    display: flex;
    justify-content: center;
    align-items: center;
    padding: ${({big}) => (big ? '16px 40px' : '14px 24px')};
    color: ${({primary}) => (primary ? '#fff' : '#000d1a')};
    font-size: ${({big}) => (big ? '20px' : '14px')};
    border-radius: ${({round}) => (round ? '50px' : '0px')};

    //medium btn
    padding: ${({medium}) => (medium ? '16px 50px' : '20px 24px')};
    font-size: ${({medium}) => (medium ? '18px' : '12px')};
    border-radius: ${({mediumround}) => (mediumround ? '12px' : '0px')};

    //small btn
    padding: ${({small}) => (small ? '13px 30px' : '14px 24px')};
    font-size: ${({small}) => (small ? '15px' : '12px')};
    border-radius: ${({smallround}) => (smallround ? '12px' : '0px')};
    &:hover {
        transform: translateY(-2px);
    }
`;
