import React,{useRef, useEffect, useCallback} from 'react';

import SlopePlan from '../../images/pistekaart saalbach.jpg';
import { GlobalStyle } from './GlobalStyle';
// import {useSpring, animated} from 'react-spring';


import {Background, ModalWrapper, ModalImg, ModalContent, CloseModalButton} from './ModalElements';




const Modal = ({showModal, setShowModal}) => {
    const modalRef = useRef();

    // const animation = useSpring({
    //     config: {
    //         duration: 250
    //     },
    //     opacity: showModal ? 1 : 0,
    //     transform: showModal ? `translateY(0%)` : `translateY(-100%)`
    // });

    const closeModal = e => {
        if(modalRef.current === e.target) {
            setShowModal(false);
        }
    };

    const keyPress = useCallback(e => {
        if(e.key === 'Escape' && showModal) {
            setShowModal(false)
        }
    }, [setShowModal, showModal])

    useEffect(() => {
        document.addEventListener('keydown', keyPress);
        return() => document.removeEventListener('keydown', keyPress);
    }, [keyPress]);



    return (
        <>
            {showModal ? (
                <Background ref={modalRef} onClick={closeModal}>
                    {/* <animated.div style={animation}> */}
                    <ModalWrapper showModal={showModal}>
                        <ModalImg src={SlopePlan} alt='slopeplan' />
                        <ModalContent>
                            {/* <h1>Saalbach Hinterglemm Leogang Fieberbrunn</h1> */}
                        </ModalContent>
                        <CloseModalButton aria-label='Close Modal' onClick={() => setShowModal(prev => !prev)}/>
                    </ModalWrapper>
                    {/* </animated.div> */}
                </Background>
            ) : null};
            <GlobalStyle/>
        </>
    );
};

export default Modal;
