import styled from 'styled-components';

export const Button = styled.button`
    //normal styling
    background: ${({primary}) => (primary ? '#000d1a' : '#cd853f')};
    white-space: nowrap;
    outline: none;
    border: none;
    min-width: 100px;
    max-width: 200px;
    cursor: pointer;
    transition: 0.3s;
    display: flex;
    justify-content: center;
    align-items: center;

    padding: ${({medium}) => (medium ? '14px 40px' : '14px 24px')};
    font-size: ${({medium}) => (medium ? '20px' : '15px')};
    border-radius: ${({mediumround}) => (mediumround ? '50px' : '0px')};

    &:hover {
        transform: translateY(-2px);
    }
`;
