import React from 'react';

import {css} from 'styled-components/macro';
import {Button} from '../Button/ButtonElements';
import {HeroSection, HeroWrapper, HeroSlide, HeroSlider, HeroImage, HeroContent} from './HeroElements';

const Hero = ({slides}) => {
    return (
        <HeroSection>
            <HeroWrapper>
                {slides.map((slide, index) => {
                    return(
                        <HeroSlide key={index}>
                            <HeroSlider>
                                <HeroImage/>
                                    <HeroContent>
                                        <h1>{slide.title}</h1>
                                        <p>{slide.altitude}</p>
                                        <Button primary='true' 
                                        css={`
                                            max-width: 160px;
                                        `}>{slide.label}
                                            <Arrow/>
                                        </Button>
                                    </HeroContent>
                            </HeroSlider>
                        </HeroSlide>
                    )
                })}
            </HeroWrapper>
        </HeroSection>
    );
};

export default Hero;
