import React, {useState} from 'react';
import { Button } from '../components/Button/ButtonMediumElements';

import Hero from '../components/Hero/Hero';
import InfoSection from '../components/InfoSection/InfoSection';
import { slopeData } from '../data/InfoData';
import {SliderDataTwo} from '../data/SliderData';

const Areas = () => {
    const [slopes, setSlopes] = useState(1);
    return (
        <>
        <Hero slides={SliderDataTwo} />
        {slopeData.filter((_,index)=> index < slopes).map((slope, index) =><InfoSection {...slope}/>)}
        {slopes === slopeData.length ? null : <> 
        <Button onClick={() => setSlopes(slopes + 1)} medium='true' mediumround='true' css={`margin: 8px;`}>Show More</Button> 
        <Button onClick={() => setSlopes(slopeData.length)} medium='true' mediumround='true' css={`margin: 8px;`}>Show All</Button></>}
        </>
    );
};

export default Areas;
