import React,{useState} from 'react';


import Hero from '../components/Hero/Hero';
import Features from '../components/Features/Features';
import { SliderData } from '../data/SliderData';
import InfoSection from '../components/InfoSection/InfoSection';
import {slopeData} from '../data/InfoData';
import Background from '../components/Background/Background';
import { Button } from '../components/Button/ButtonMediumElements';


const Home = () => {
const [slopes, setSlopes] = useState(1);

    return (
        <>
        <Background/>
        <Hero slides={SliderData}/>
        <Features/>
        {slopeData.filter((_,index)=> index < slopes).map((slope, index) =><InfoSection {...slope}/>)}
        {slopes === slopeData.length ? null : <> 
        <Button onClick={() => setSlopes(slopes + 1)} medium='true' mediumround='true'>Show More</Button> 
        <Button onClick={() => setSlopes(slopeData.length)}>Show All</Button></>}

   </>
    );
};



export default Home;
